client_script:
frappe.ready(() => {

    // Autocomplete for Item Name
    const field = $('[data-fieldname="item_name"] input');

    field.attr("autocomplete", "off");

    field.on("input", function () {
        let query = $(this).val();

        frappe.call({
            method: "frappe.client.get_list",
            args: {
                doctype: "Item",
                fields: ["item_name"],
                filters: [["item_name", "like", "%" + query + "%"]],
                limit_page_length: 10
            },
            callback: function (r) {

                let results = r.message || [];
                let listId = "auto-item-name-list";

                $("#" + listId).remove();

                let box = $(`<div id='${listId}'
                    style="
                        border: 1px solid #ccc;
                        background: white;
                        position: absolute;
                        width: 100%;
                        max-height: 150px;
                        overflow-y: auto;
                        border-radius: 6px;
                        z-index: 9999;
                    ">
                </div>`);

                results.forEach(item => {
                    let opt = $(`<div style="padding: 8px; cursor: pointer;">
                        ${item.item_name}
                    </div>`);
                    opt.click(() => {
                        field.val(item.item_name);
                        box.remove();
                    });
                    box.append(opt);
                });

                field.after(box);
            }
        });
    });

});

custom_css:
/* ===========================
   NAVBAR GRADIENT (top strip)
   =========================== */
.navbar {
    background: linear-gradient(90deg, #004aad, #007bff) !important;
}

.navbar .navbar-brand,
.navbar .nav-link {
    color: white !important;
}

/* ===========================
   FULL HEADER BLOCK COLOR
   =========================== */
.web-form-header {
    background: linear-gradient(90deg, #004aad, #007bff) !important;
    padding: 35px !important;
    border-radius: 12px !important;
    text-align: center !important;
    color: white !important;
    position: relative !important;
}

/* FORCE CENTER TITLE */
.web-form-header h1 {
    background: transparent !important;
    color: white !important;
    font-size: 28px !important;
    font-weight: 700 !important;
    margin: 0 !important;
    width: 100% !important;
    text-align: center !important;
    justify-content: center !important;
    align-items: center !important;
    display: flex !important;
    gap: 8px !important;
}

/* Move the "Not Saved" pill so it doesn't push the title */
.web-form-header .indicator-pill {
    position: absolute !important;
    right: 20px !important;
    top: 20px !important;
}

.web-form-header p {
    color: white !important;
    font-size: 16px !important;
    margin-top: 8px !important;
}

/* ===========================
   REQUEST ITEM TABLE HEADER
   =========================== */
.grid-heading-row,
.grid-heading-row th {
    background: linear-gradient(90deg, #004aad, #007bff) !important;
    color: white !important;
}

/* ===========================
   BUTTON COLORS
   =========================== */
.btn-primary,
.btn-default,
.btn-secondary {
    background: linear-gradient(90deg, #004aad, #007bff) !important;
    border-color: #004aad !important;
    color: white !important;
}

/* ===========================
   UNIVERSAL BORDER FOR ALL FIELDS
   =========================== */
.web-form-container input,
.web-form-container select,
.web-form-container textarea,
.web-form-container .form-control {
    border: 1.8px solid #004aad !important;
    border-radius: 6px !important;
    padding: 8px 10px !important;
    background: #ffffff !important;
    opacity: 1 !important;
}

/* Frappe control wrappers (read-only, auto-fetch, disabled) */
.frappe-control input,
.frappe-control textarea,
.frappe-control select {
    border: 1.8px solid #004aad !important;
    border-radius: 6px !important;
    padding: 8px 10px !important;
    background: #ffffff !important;
    opacity: 1 !important;
}

/* Disabled fields */
input[readonly],
textarea[readonly],
input:disabled,
textarea:disabled {
    border: 1.8px solid #004aad !important;
    border-radius: 6px !important;
    background: #ffffff !important;
    opacity: 1 !important;
}

/* Child table fields */
.grid-row input,
.grid-row select,
.grid-row textarea {
    border: 1.8px solid #004aad !important;
    border-radius: 6px !important;
    background: #ffffff !important;
}

/* Select2 dropdown */
.select2-container .select2-selection {
    border: 1.8px solid #004aad !important;
    border-radius: 6px !important;
    background: #ffffff !important;
}
